mapper.update:
UPDATE meeting
SET content=#{content}
WHERE id = #{id}

mapper.select:
        SELECT * FROM meetingThumbnail


				 id >= #{startId} 

						 id < #{startId} 




					AND closedAt IS NULL
					 AND startedAt >= NOW() 


					AND closedAt IS NOT NULL



				AND categoryId = #{categoryId}


				AND regionId IN

					#{regionId}



				AND title LIKE '%${keyword}%'


        LIMIT 0, 9;

mapper.update:
UPDATE meeting
SET viewCount = viewCount + 1
WHERE id = #{id}

UPDATE meeting
SET commentCount = commentCount + 1
WHERE id = #{id}

UPDATE meeting
SET commentCount = commentCount - 1
WHERE id = #{id}
