DATEDIFF(createdAt, #{minDate}) <= 0 AND DATEDIFF(createdAt, #{minDate}) >= - #{period}

SELECT id, profileImagePath, nickname, fame, isSuspended, createdAt, resignedAt FROM member
nickname LIKE '%${keyword}%'
id = #{keyword}
DATEDIFF(createdAt, #{minDate}) <= 0 AND DATEDIFF(createdAt, #{minDate}) >= - #{period}
ORDER BY id DESC
ORDER BY id ASC
LIMIT #{offset},#{size};

